function update_notif(jenis) {
  $(".modal-content")
    .removeClass("errorbox")
    .removeClass("successbox")
    .removeClass("infobox")
    .addClass(jenis);
};

function set_error(msg) {
    update_notif("errorbox");
    $(".modal-title").html("Terjadi Kesalahan!");
    $("#modal-msg").html(msg);
    $("#myModal").modal("show");
};

function validasi_teks(input) {
  $key = input.which;
  if(
      $key>=65 && $key<=90 || 
      $key>=97 && $key<=122 ||
      $key==32 || $key==8 || $key==0 || $key==13
      ) {return true;} else {
    set_error("Inputan hanya diperbolehkan berjenis teks!");
    return false;
}
  // 65 - 90  => A - Z
  // 97 - 122 => a - z
  // 32 => Spasi
  // 8  => Backspace
  // 0  => Panah : atas, bawah, kiri, kanan
  // 13 => Enter
};

function validasi_number(input) {
  $key = input.which;
  console.log($key);
  if(
      $key>=48 && $key<=57 ||
      $key==32 || $key==8 || $key==0 || $key==13
    ) {return true;} else {
    set_error("Inputan hanya diperbolehkan berjenis Angka!");
    return false;
  }
};

