function tampilkan_error(msg) {
    update_notif("errorbox");
    $("#modal-msg").html(msg);
    $("#myModal").modal("show");
}

function tampilkan_success(msg) {
    update_notif("successbox");
    $("#modal-msg").html(msg);
    $("#myModal").modal("show");
}

function tampilkan_warning(msg) {
    update_notif("warningbox");
    $("#modal-msg").html(msg);
    $("#myModal").modal("show");
}

function notif($tipe,$msg) {
	switch($tipe) {
		case "error"   : $(".modal-title").html("Terjadi Kesalahan!");tampilkan_error($msg);break;
		case "success" : $(".modal-title").html("Selamat!");tampilkan_success($msg);break;
		default        : $(".modal-title").html("Perhatian!");tampilkan_warning($msg);break;
	};
}