/* Perlu Plugin penampil pesan */

function getThisYear() {
    var today = new Date();
    return today.getFullYear();
}

function getThisDate() {
    var today = new Date();
    var dd = today.getDate();
    return dd;
}

function getThisMonth() {
    var today = new Date();
    var mm = today.getMonth();
    return mm;
}

function validasi(event) {
    // Ambil Tahun Lahir //
    $tglLahir = $("input[name=tglLahir]").val();
    $tglLahir = new Date($tglLahir);
    // Ambil Tahun Lahir //

    // Cek Jadi Dosen/Etc //
    $tglJadi = $("input[name=tglJadi]").val();
    $tglJadi = new Date($tglJadi);
    // Cek Jadi Dosen/Etc //


    if($tglJadi.getFullYear()>getThisYear()) {
        notif("error","Tahun pertama kali memperoleh jabatan tidak valid!");
        return false;
    } else
    if(getThisYear()==$tglJadi.getFullYear()) {
        if( $tglJadi.getMonth() > getThisMonth() ) {
            notif("error","Tanggal pertama kali memperoleh jabatan tidak valid!");
            return false;
        } else
        if( $tglJadi.getMonth() == getThisMonth()) {
            if($tglJadi.getDate()>getThisDate()) {
                notif("error","Tanggal pertama kali memperoleh jabatan tidak valid!");
                return false;
            };
        };
        // Validasi Tanggal Lahir //
        if(getThisYear()-$tglLahir.getFullYear() >=20) { //Jika umurnya >20 thn;
            return true;
        } else {
            notif("error","Tanggal lahir yang diinputkan terlalu dini!");
            return false;
        };
    };
}